      squares: ["ðŸ‘â€ðŸ—¨", "ðŸ‘â€ðŸ—¨", "ðŸ‘â€ðŸ—¨", "ðŸ‘â€ðŸ—¨", "ðŸ‘â€ðŸ—¨", "ðŸ‘â€ðŸ—¨", "ðŸ‘â€ðŸ—¨", "ðŸ‘â€ðŸ—¨", "ðŸ‘â€ðŸ—¨"],
      activePlayer: "player1",
      winStatus: "",
      gameStatus: false,
      gameOver: "",
      click: true


    }
  }

handleGamePlay = (index) => {
  const {squares, activePlayer, gameStatus} = this.state
  if (this.state.gameStatus === true){
    this.setState({squares: squares, gameOver : "The game is over" })
    // {squares[index] = null}
} else if (activePlayer === "player1" || squares[index]==="ðŸ‘¾"){
    squares[index] = "ðŸ‘¾"
    this.setState({squares: squares})
} else if (activePlayer === "player2" || squares[index]==="ðŸ›¸"){
    squares[index] = "ðŸ›¸"
    this.setState({squares: squares})
} 

  const winning =[
    [0,1,2],
    [3,4,5],
    [6,7,8],
    [0,3,6],
    [1,4,7],
    [2,5,8],
    [6,4,2],
    [0,4,8],
  ]
  for(let i=0; i<winning.length; i++){
    const [a, b, c] = winning[i];
    // console.log("here")
    // console.log(a)
    // console.log(b)
    // console.log(c)
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c] && squares[a] === "ðŸ‘¾"){
    this.setState({winStatus: "player1"})
    console.log("PLAYER ONE")
